import React from "react";

function Aboutme() {
  return (
    <div className="shadow-lg border-dark card my-2" id="darkTheme">
      <div className="card-body">
        <div className="my-2 p-0">
          <div className="">
            <h5 className="textLight">ABOUT ME</h5>
          </div>
        </div>
        <div className="my-2">
          <div className="">
            <p className="text-secondary textParam">Hello I'm John Paul a Website Developer, I start learning web development went I was in the third year, and I love it because it is fun doing, and learning something new makes me happy.</p>
            <p className="text-secondary textParam">Today I'm deeply invested in life-long learning, I'm a front-end developer who carries a keen eye for design in a user-first approach. My focus is on creating a wireframe, mockups, and responsive layouts on the HTML, CSS, and JavaScript, and in the future is with bootstrap, React, NodeJS, and more. and from my background as a BS Computer Science student, I'm well acquainted with bringing out solutions to complex problems.</p>
            <p className="text-secondary textParam">I LOVE Solving a problem and being part of a group that creates valuable things, I'm a person more on focus on long-term goals because it makes me disciplined, patient and a person with a reason to keep going on what I'm doing.</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Aboutme;
